    if (c === "-" && !newExtension){
      newExtension = true;
      e = {
        'singleton': null,
        'extension': []
      };
      continue;
    }
		
    if (newExtension && !singleton){
      singleton = true;
      e['singleton'] = c;
      continue;
    }
		
