      // just create a dummy row for deletion
      row = new oj.ArrayRow({}, null);
      row.index = rowIdx;
      rowSet._rows.splice(rowIdx, 1);
      oj.FlattenedTreeRowSet.superclass._handleEvent.call(rowSet, oj.RowSet.EventType['REMOVE'], row);
