      var ev = e['originalEvent']['originalEvent'];

      if (ev['type'] === 'keydown') {
        this._menu.tab = this.active;
      }
      else  {
        this._menu.tab = $(e.originalEvent.target).closest("li");
      }
      this._menu.tabId = this._menu.tab.attr("id");

